        // Detect if on mobile (screen width <= 767px) to adjust circumference
        const isMobile = window.innerWidth <= 767;
        const circumference = isMobile ? 141.37 : 157.08; // Mobile radius 22.5, desktop radius 25

        // Adjust total values for better visual representation
        const totalDays = 30;
        const totalHours = 24;
        const totalMinutes = 60;
        const totalSeconds = 60;

        // Calculate progress and ensure full circle when value is 0
        const daysProgress = parseInt(days) === 0 ? 1 : parseInt(days) / totalDays;
        const hoursProgress = parseInt(hours) === 0 ? 1 : parseInt(hours) / totalHours;
        const minutesProgress = parseInt(minutes) === 0 ? 1 : parseInt(minutes) / totalMinutes;
        const secondsProgress = parseInt(seconds) === 0 ? 1 : parseInt(seconds) / totalSeconds;

        const daysPercent = daysProgress * circumference;
        const hoursPercent = hoursProgress * circumference;
        const minutesPercent = minutesProgress * circumference;
        const secondsPercent = secondsProgress * circumference;

        document.getElementById("days-circle").style.strokeDashoffset = circumference - daysPercent;
        document.getElementById("hours-circle").style.strokeDashoffset = circumference - hoursPercent;
        document.getElementById("minutes-circle").style.strokeDashoffset = circumference - minutesPercent;
        document.getElementById("seconds-circle").style.strokeDashoffset = circumference - secondsPercent;

        if (timeRemaining <= 0) {
          clearInterval(timer);
          document.getElementById("days").textContent = "00";
          document.getElementById("hours").textContent = "00";
          document.getElementById("minutes").textContent = "00";
          document.getElementById("seconds").textContent = "00";
          document.getElementById("days-circle").style.strokeDashoffset = 0;
          document.getElementById("hours-circle").style.strokeDashoffset = 0;
          document.getElementById("minutes-circle").style.strokeDashoffset = 0;
          document.getElementById("seconds-circle").style.strokeDashoffset = 0;
          document.querySelector(".timer-label").textContent = "EXPIRED";
          document.querySelector(".timer-section").style.background = "#777777";
        }
      };

      const timer = setInterval(countdown, 1000);
      countdown(); // Initial call to set the timer immediately
    });
  </script>
</body>
</html>
{% endif %}
